import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async () => {
        // Валидация полей
        if (!username.trim() || !password.trim()) {
            setError('Please fill in all fields');
            return;
        }

        setIsLoading(true);
        setError('');

        try {
            // 1. Отправляем запрос на правильный эндпоинт
            const response = await axios.post('http://localhost:8081/api/v1/auth/login', {
                username,
                password
            });

            // 2. Обрабатываем структуру ответа
            const { token, user } = response.data;

            // 3. Сохраняем данные в localStorage
            localStorage.setItem('token', token);
            localStorage.setItem('userId', user.id.toString()); // Преобразуем в строку
            localStorage.setItem('username', user.username);

            // 4. Перенаправляем на страницу постов
            navigate('/posts');
        } catch (error) {
            // 5. Улучшенная обработка ошибок
            const errorMessage = error.response?.data?.message 
                || error.response?.data?.error 
                || 'Login failed. Please try again.';

            setError(errorMessage);
            console.error('Login error:', error);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="auth-container">
            <h2>Login</h2>

            {/* Отображение ошибок */}
            {error && <div className="error-message">{error}</div>}

            <input
                type="text"
                className="form-control"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                disabled={isLoading}
            />

            <input
                type="password"
                className="form-control"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                disabled={isLoading}
            />

            <button 
                className="btn btn-primary" 
                onClick={handleLogin}
                disabled={isLoading}
            >
                {isLoading ? 'Loading...' : 'Login'}
            </button>
        </div>
    );
};

export default Login;
