// src/Chat.js
import React, { useState, useEffect } from 'react';
import useWebSocket from 'react-use-websocket';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const { sendMessage, lastMessage } = useWebSocket('ws://localhost:8082/ws');
  let usernames = localStorage.getItem("username")

  useEffect(() => {
    if (lastMessage !== null) {
      setMessages((prevMessages) => [...prevMessages, lastMessage.data]);
    }
  }, [lastMessage]);

  const handleSendMessage = () => {
    sendMessage(JSON.stringify({ username: usernames, message }));
    setMessage('');
  };

  return (
    <div>
      <div>
        {messages.map((msg, index) => {
          const parsedMsg = JSON.parse(msg);
          return (
            <div key={index}>
              <strong>{parsedMsg.username}:</strong> {parsedMsg.message}
            </div>
          );
        })}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default Chat;
