import React, { useState } from 'react';
import useWebSocket from '../../hooks/useWebSocket'; // проверь путь
import '../../components/MainLayout.css'; // проверь путь

const Chat = () => {
  const token = localStorage.getItem('token');
  const [message, setMessage] = useState('');

  const {
    messages,
    sendMessage,
    isConnected,
    connectionStatus,
    error,
  } = useWebSocket('ws://localhost:8082/chat');

  const handleSend = () => {
    if (message.trim()) {
      sendMessage({ text: message });
      setMessage('');
    }
  };

  if (!token) {
    return (
      <div style={{ padding: '2rem', color: 'red', textAlign: 'center' }}>
        Доступ запрещен. Пожалуйста, войдите в систему.
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Чат</h2>

      {!isConnected && (
        <p style={{ color: 'orange' }}>
          Статус подключения: {connectionStatus}
        </p>
      )}

      {error && (
        <p style={{ color: 'red' }}>
          Ошибка: {error}
        </p>
      )}

      <div style={{ marginBottom: '1rem' }}>
        {messages.map((msg, index) => (
          <div key={index} style={{ marginBottom: '0.5rem' }}>
            <strong>{msg.username || 'Аноним'}:</strong> {msg.text}
          </div>
        ))}
      </div>

      <div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Введите сообщение"
          style={{ padding: '0.5rem', width: '60%' }}
        />
        <button
          onClick={handleSend}
          style={{ padding: '0.5rem', marginLeft: '1rem' }}
          disabled={!isConnected}
        >
          Отправить
        </button>
      </div>
    </div>
  );
};

export default Chat;


