import React, { useState, useEffect } from 'react';
import axios from 'axios';
 import useWebSocket from '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/hooks/useWebSocket.js'; // путь поменяй если другой
 import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css'; // путь к стилям


const Chat = () => {
  const [message, setMessage] = useState('');
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(null);
  const [username, setUsername] = useState(null);
  const [isSending, setIsSending] = useState(false);

  const {
    messages,
    sendMessage,
    connectionStatus,
    connect,
    disconnect,
    isConnected,
    error: wsError
  } = useWebSocket('ws://localhost:8082/ws', { manual: true });

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    const storedUsername = localStorage.getItem('username');

    if (storedUserId && storedUsername) {
      setUserId(storedUserId);
      setUsername(storedUsername);
      setIsLoggedIn(true);
    }
  }, []);

  useEffect(() => {
    if (isLoggedIn) {
      connect();
    } else {
      disconnect();
    }

    return () => disconnect();
  }, [isLoggedIn, connect, disconnect]);

  useEffect(() => {
    if (wsError) {
      setErrorMessage(wsError);
    }
  }, [wsError]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setErrorMessage('');

    try {
      const response = await axios.post('http://localhost:8080/login', {
        username: login,
        password
      });

      const { userId, username, token } = response.data;

      if (!userId || !username) {
        throw new Error('Invalid server response');
      }

      localStorage.setItem('userId', userId);
      localStorage.setItem('username', username);
      localStorage.setItem('token', token);

      setUserId(userId);
      setUsername(username);
      setIsLoggedIn(true);
    } catch (error) {
      console.error("Login error:", error);
      setErrorMessage(error.response?.data?.error || 'Login failed');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');

    if (!isConnected) {
      setErrorMessage('Connection not ready. Please wait...');
      return;
    }

    if (!message.trim()) {
      setErrorMessage('Message cannot be empty');
      return;
    }

    try {
      setIsSending(true);
      await sendMessage({
        user_id: userId,
        username,
        content: message.trim()
      });
      setMessage('');
    } catch (error) {
      console.error('Send message error:', error);
      setErrorMessage(error.message);
    } finally {
      setIsSending(false);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="auth-container">
        <h2>Login to use Chat</h2>
        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Username</label>
            <input
              type="text"
              value={login}
              onChange={(e) => setLogin(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button 
            type="submit" 
            className="login-button"
            disabled={!login || !password}
          >
            Login
          </button>
          {errorMessage && <div className="error-message">{errorMessage}</div>}
        </form>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <div className="header">
        <div className="connection-status">
          Status: {connectionStatus}
          {!isConnected && <span className="connecting-dots">...</span>}
        </div>
        <button 
          className="logout-button"
          onClick={() => {
            disconnect(true);
            localStorage.clear();
            setIsLoggedIn(false);
          }}
        >
          Logout
        </button>
      </div>

      {!isConnected ? (
        <div className="connection-loader">
          <div className="spinner"></div>
          <p>Connecting to chat...</p>
        </div>
      ) : (
        <>
          <div className="messages">
            {messages.map((msg, index) => (
              <div 
                key={`msg-${index}-${msg.timestamp}`} 
                className="message"
              >
                <span className="username">{msg.username}:</span>
                <span className="content">{msg.content}</span>
                <span className="timestamp">
                  {new Date(msg.timestamp).toLocaleTimeString()}
                </span>
              </div>
            ))}
          </div>

          <form onSubmit={handleSubmit} className="message-form">
            <input
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Type your message..."
              disabled={!isConnected || isSending}
            />
            <button 
              type="submit" 
              disabled={!isConnected || isSending || !message.trim()}
              className="send-button"
            >
              {isSending ? (
                <>
                  <span className="sending-spinner"></span>
                  Sending...
                </>
              ) : 'Send'}
            </button>
          </form>
        </>
      )}

      {errorMessage && <div className="error-message">{errorMessage}</div>}
    </div>
  );
};

