import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Comments = ({ postId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [submitting, setSubmitting] = useState(false);

    const token = localStorage.getItem('token');
    const isAuthenticated = !!token;
    const userData = localStorage.getItem('user');
    const currentUser = userData ? JSON.parse(userData) : null;
    const navigate = useNavigate();

    const fetchComments = useCallback(async () => {
        try {
            setLoading(true);
            setError(null);

            const config = isAuthenticated ? {
                headers: { 
                    'Authorization': `Bearer ${token}`
                }
            } : {};

            const response = await axios.get(
                `http://localhost:8081/api/v1/posts/${postId}/comments`,
                config
            );

            // Обработка разных форматов ответа
            const rawData = response.data;
            let commentsArray = [];

            if (Array.isArray(rawData)) {
                commentsArray = rawData;
            } else if (rawData && Array.isArray(rawData.data)) {
                commentsArray = rawData.data;
            } else if (rawData && Array.isArray(rawData.comments)) {
                commentsArray = rawData.comments;
            }

            const processedComments = commentsArray.map(comment => ({
                id: comment.id ? parseInt(comment.id, 10) : Date.now(),
                author_id: comment.author_id ? parseInt(comment.author_id, 10) : 0,
                post_id: comment.post_id ? parseInt(comment.post_id, 10) : 0,
                content: comment.content || '',
                author_name: comment.author_name || `User #${comment.author_id || 'unknown'}`,
                created_at: comment.created_at || new Date().toISOString()
            }));

            setComments(processedComments);
        } catch (err) {
            setError(err.response?.data?.error || err.message || 'Failed to load comments');
        } finally {
            setLoading(false);
        }
    }, [postId, token, isAuthenticated]);

    const handleSubmitComment = async (e) => {
        e.preventDefault();

        if (!isAuthenticated || !currentUser) {
            navigate('/login');
            return;
        }

        if (!newComment.trim() || submitting) return;

        try {
            setSubmitting(true);
            setError(null);

            const response = await axios.post(
                `http://localhost:8081/api/v1/posts/${postId}/comments`,
                { 
                    content: newComment,
                    author_id: currentUser.id
                },
                { 
                    headers: { 
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'application/json' 
                    }
                }
            );

            // Добавление нового комментария с полными данными
            const newCommentWithAuthor = {
                id: response.data.id || Date.now(),
                author_id: currentUser.id,
                post_id: postId,
                content: newComment,
                author_name: currentUser.username || `User #${currentUser.id}`,
                created_at: response.data.created_at || new Date().toISOString()
            };

            setComments(prev => [newCommentWithAuthor, ...prev]);
            setNewComment('');
        } catch (err) {
            setError(err.response?.data?.error || err.message || 'Failed to post comment');
        } finally {
            setSubmitting(false);
        }
    };

    useEffect(() => {
        if (postId) {
            fetchComments();
        }
    }, [postId, fetchComments]);

    return (
        <div className="comments-section">
            <h4>Comments ({comments.length})</h4>

            {isAuthenticated && currentUser ? (
                <form onSubmit={handleSubmitComment} className="comment-form">
                    <textarea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="Write a comment..."
                        rows="3"
                        disabled={submitting}
                        required
                    />
                    <button 
                        type="submit" 
                        className="submit-comment-btn"
                        disabled={submitting || !newComment.trim()}
                    >
                        {submitting ? 'Posting...' : 'Post Comment'}
                    </button>
                </form>
            ) : (
                <div className="auth-prompt">
                    <p>Please <a href="/login">login</a> to post comments</p>
                </div>
            )}

            {loading && <div className="loading-indicator">Loading comments...</div>}
            {error && <div className="error-message">Error: {error}</div>}

            <div className="comments-list">
                {comments.map(comment => (
                    <div key={comment.id} className="comment-item">
                        <div className="comment-header">
                            <span className="comment-author">
                                {comment.author_name}
                            </span>
                            <span className="comment-timestamp">
                                {new Date(comment.created_at).toLocaleDateString('en-US', {
                                    hour: '2-digit',
                                    minute: '2-digit',
                                    day: 'numeric',
                                    month: 'short',
                                    year: 'numeric'
                                })}
                            </span>
                        </div>
                        <div className="comment-content">
                            {(comment.content || '').split('\n').map((line, index) => (
                                <p key={index}>{line}</p>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Comments;
