import { useState, useRef, useCallback } from 'react';

const useWebSocket = (url, { manual = false } = {}) => {
  const [messages, setMessages] = useState([]);
  const [connectionStatus, setConnectionStatus] = useState('disconnected'); // 'connecting', 'connected', 'disconnected'
  const [error, setError] = useState(null);
  const wsRef = useRef(null);
  const reconnectRef = useRef(false);

  const connect = useCallback(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No token found');
      return;
    }

    const ws = new WebSocket(`${url}?token=${token}`);
    wsRef.current = ws;
    setConnectionStatus('connecting');
    setError(null);

    ws.onopen = () => {
      setConnectionStatus('connected');
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        setMessages((prev) => [...prev, data]);
      } catch (err) {
        console.error('Invalid WebSocket message format:', err);
      }
    };

    ws.onerror = (err) => {
      setError('WebSocket error');
      console.error('WebSocket error:', err);
    };

    ws.onclose = (event) => {
      setConnectionStatus('disconnected');
      wsRef.current = null;

      // Only retry if explicitly allowed
      if (reconnectRef.current) {
        setTimeout(() => connect(), 3000);
      }
    };
  }, [url]);

  const disconnect = useCallback((autoReconnect = false) => {
    reconnectRef.current = autoReconnect;
    if (wsRef.current) {
      wsRef.current.close();
    }
  }, []);

  const sendMessage = useCallback((msg) => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify(msg));
    } else {
      setError('Cannot send message: WebSocket not connected');
    }
  }, []);

  if (!manual && !wsRef.current && connectionStatus === 'disconnected') {
    connect();
  }

  return {
    messages,
    sendMessage,
    connectionStatus,
    connect,
    disconnect,
    error,
  };
};

export default useWebSocket;

