import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import useWebSocket from '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/hooks/useWebSocket.js';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const {
    messages,
    sendMessage,
    connectionStatus,
    connect,
    disconnect,
    error: wsError
  } = useWebSocket('ws://localhost:8082/ws', { manual: true });

  // Проверка авторизации при монтировании
  useEffect(() => {
    const checkConnection = () => {
      if (
        connectionStatus === 'disconnected' && 
        isLoggedIn &&
        !reconnectTimeoutRef.current
      ) {
        console.log('Triggering reconnect...');
        connect();
      }
    };

    const interval = setInterval(checkConnection, 5000);
    return () => clearInterval(interval);
  }, [connectionStatus, isLoggedIn, connect]);

  // Автоподключение и управление соединением
  useEffect(() => {
    if (isLoggedIn) {
      connect();
    }

    return () => {
      if (isLoggedIn) {
        disconnect(true);
      }
    };
  }, [isLoggedIn, connect, disconnect]);

  // Механизм переподключения
  useEffect(() => {
    const reconnectInterval = setInterval(() => {
      if (connectionStatus === 'disconnected' && isLoggedIn) {
        connect();
      }
    }, 5000);

    return () => clearInterval(reconnectInterval);
  }, [connectionStatus, isLoggedIn, connect]);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/login', {
        email,
        password,
      });

      const { token, userId, username } = response.data;

      if (!token || !userId || !username) {
        throw new Error('Invalid server response');
      }

      localStorage.setItem('token', token);
      localStorage.setItem('userId', userId);
      localStorage.setItem('username', username);

      setIsLoggedIn(true);
      setErrorMessage('');

    } catch (error) {
      console.error("Login error:", error);
      setErrorMessage(error.response?.data?.message || 'Login failed. Please try again.');
      localStorage.clear();
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();

    const userId = localStorage.getItem('userId');
    const username = localStorage.getItem('username');

    if (!userId || !username) {
      setErrorMessage('User session expired');
      localStorage.clear();
      setIsLoggedIn(false);
      return;
    }

    if (!message.trim()) {
      setErrorMessage('Cannot send empty message');
      return;
    }

    sendMessage({
      content: message.trim(),
      userId: parseInt(userId, 10),
      username,
    });

    setMessage('');
    setErrorMessage('');
  };

  if (!isLoggedIn) {
    return (
      <div className="auth-container">
        <h2>Login to use Chat</h2>
        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn-primary">Login</button>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
        </form>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <div className="connection-status">
        Connection Status: {connectionStatus}
        {wsError && <span className="error-indicator"> • Error</span>}
      </div>

      {wsError && <p className="error-message">{wsError}</p>}

      <div className="messages-panel">
        {messages.map((msg, index) => (
          <div key={index} className="message">
            <span className="message-username">{msg.username}:</span>
            <span className="message-content">{msg.content}</span>
            <span className="message-timestamp">
              {new Date(msg.timestamp).toLocaleTimeString()}
            </span>
          </div>
        ))}
      </div>

      <form onSubmit={handleSendMessage} className="message-form">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          className="message-input"
        />
        <button 
          type="submit" 
          className="send-button"
          disabled={connectionStatus !== 'connected'}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default Chat;
