// src/Chat.js
import React, { useState, useEffect } from 'react';
import useWebSocket from 'react-use-websocket';
import axios from 'axios';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const { sendMessage, lastMessage } = useWebSocket('ws://localhost:8082/ws');

  useEffect(() => {
    // Загрузка сообщений из базы данных при инициализации
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:8082/messages');
        setMessages(response.data);
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, []);

  useEffect(() => {
    if (lastMessage !== null) {
      setMessages((prevMessages) => [...prevMessages, JSON.parse(lastMessage.data)]);
    }
  }, [lastMessage]);

  const handleSendMessage = () => {
    const msg = { username: 'User', message };
    sendMessage(JSON.stringify(msg));
    setMessage('');
  };

  return (
    <div>
      <div>
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.username}:</strong> {msg.message}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default Chat;
