import React, { useState, useEffect } from 'react';
import useWebSocket from '/path/to/useWebSocket';
import './MainLayout.css';

const Chat = () => {
    const [message, setMessage] = useState('');
    const userId = localStorage.getItem('userId');
    const token = localStorage.getItem('token');
    const wsUrl = `ws://localhost:8082/ws`;

    const { 
        messages, 
        sendMessage, 
        connectionStatus 
    } = useWebSocket(wsUrl);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim() && userId) {
            sendMessage({
                text: message,
                userId: userId,
                createdAt: new Date().toISOString()
            });
            setMessage('');
        }
    };

    if (!userId) {
        return <div className="auth-warning">Please login to use chat</div>;
    }

    return (
        <div className="chat-container">
            <div className={`status ${connectionStatus}`}>
                {connectionStatus.toUpperCase()}
            </div>

            <div className="messages">
                {messages.map((msg, index) => (
                    <div key={index} className="message">
                        <div className="message-header">
                            <span className="user">{msg.userId}</span>
                            <span className="time">
                                {new Date(msg.createdAt).toLocaleTimeString()}
                            </span>
                        </div>
                        <div className="content">{msg.text}</div>
                    </div>
                ))}
            </div>

            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    disabled={connectionStatus !== 'connected'}
                />
                <button 
                    type="submit"
                    disabled={connectionStatus !== 'connected' || !message.trim()}
                >
                    Send
                </button>
            </form>
        </div>
    );
};

export default Chat;
