import React, { useState, useEffect } from 'react';
import axios from 'axios';
import useWebSocket from '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/hooks/useWebSocket.js';
import './MainLayout.css';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(null);
  const [username, setUsername] = useState(null);

  const {
    messages,
    sendMessage,
    connectionStatus,
    connect,
    disconnect
  } = useWebSocket('', {
    manual: true,
    reconnectInterval: 10000
  });

  // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ñ Ð´ÑƒÐ±Ð»Ð¸ÐºÐ°Ñ‚Ð¾Ð² Ð¿Ð¾ ID ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ
  const uniqueMessages = Array.from(
    new Map(messages.map(msg => [msg.id, msg])).values()
  );

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    const storedUsername = localStorage.getItem('username');
    const token = localStorage.getItem('token');

    if (storedUserId && storedUsername && token) {
      setUserId(storedUserId);
      setUsername(storedUsername);
      setIsLoggedIn(true);
      const wsUrl = `ws://localhost:8082/ws?token=${encodeURIComponent(token)}`;
      connect(wsUrl);
    }

    return () => disconnect();
  }, [connect, disconnect]);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/login', {
        email,
        password,
      });

      if (response.status === 200) {
        const { userId, username, token } = response.data;

        if (!userId || !username || !token) {
          setErrorMessage('Invalid server response');
          return;
        }

        localStorage.setItem('userId', userId);
        localStorage.setItem('username', username);
        localStorage.setItem('token', token);

        setUserId(userId);
        setUsername(username);
        setIsLoggedIn(true);

        const wsUrl = `ws://localhost:8082/ws?token=${encodeURIComponent(token)}`;
        connect(wsUrl);
      }
    } catch (error) {
      console.error('Login error:', error);
      setErrorMessage(error.response?.data?.message || 'Login failed');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!message.trim() || !username) return;

    const success = sendMessage({
      id: Date.now(), // Ð“ÐµÐ½ÐµÑ€Ð°Ñ†Ð¸Ñ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾Ð³Ð¾ ID
      user_id: userId,
      username: username,
      content: message.trim(),
      timestamp: new Date().toISOString()
    });

    setMessage(success ? '' : message);
    if (!success) {
      setErrorMessage('Failed to send message');
      setTimeout(() => setErrorMessage(''), 3000);
    }
  };

  const handleReconnect = () => {
    const token = localStorage.getItem('token');
    if (token) {
      const wsUrl = `ws://localhost:8082/ws?token=${encodeURIComponent(token)}`;
      connect(wsUrl);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="login-container">
        <h2>Login to Chat</h2>
        <form onSubmit={handleLogin} className="login-form">
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="form-input"
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="form-input"
            />
          </div>
          <button type="submit" className="login-button">
            Login
          </button>
          {errorMessage && <div className="error-message">{errorMessage}</div>}
        </form>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <div className="status-bar">
        <div className={`connection-status ${connectionStatus}`}>
          {{
            connected: 'ðŸŸ¢ Connected',
            connecting: 'ðŸŸ¡ Connecting...',
            reconnecting: 'ðŸŸ  Reconnecting...',
            error: 'ðŸ”´ Connection Error',
            disconnected: 'âš« Disconnected'
          }[connectionStatus]}
        </div>
        <button
          onClick={handleReconnect}
          disabled={connectionStatus === 'connected'}
          className="reconnect-button"
        >
          Reconnect
        </button>
      </div>

      <div className="messages">
        {uniqueMessages.map((msg) => (
          <div key={msg.id} className="message">
            <span className="message-user">{msg.username}:</span>
            <span className="message-content">{msg.content}</span>
            <span className="message-time">
              {new Date(msg.timestamp).toLocaleTimeString([], {
                hour: '2-digit',
                minute: '2-digit'
              })}
            </span>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} className="message-form">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          disabled={connectionStatus !== 'connected'}
          className="message-input"
          aria-label="Message input"
        />
        <button
          type="submit"
          disabled={connectionStatus !== 'connected' || !message.trim()}
          className="send-button"
          aria-label="Send message"
        >
          Send
        </button>
      </form>

      {errorMessage && <div className="error-message">{errorMessage}</div>}
    </div>
  );
};

export default Chat;
