// src/components/Chat/Chat.jsx
import React, { useState, useEffect, useCallback } from 'react';
import './Chat.css';

const useChat = () => {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [error, setError] = useState(null);

  const connectWebSocket = useCallback((token) => {
    const ws = new WebSocket(`ws://localhost:8080/ws?token=${encodeURIComponent(token)}`);

    ws.onopen = () => {
      setConnectionStatus('connected');
      setError(null);
    };

    ws.onmessage = (event) => {
      const message = JSON.parse(event.data);
      setMessages(prev => [...prev, message]);
    };

    ws.onclose = (event) => {
      setConnectionStatus('disconnected');
      if (!event.wasClean) {
        setError('Connection lost. Reconnecting...');
        setTimeout(() => connectWebSocket(token), 3000);
      }
    };

    ws.onerror = (error) => {
      setError('WebSocket error: ' + error.message);
      setConnectionStatus('error');
    };

    setSocket(ws);
    return () => ws.close();
  }, []);

  const sendMessage = (messageText) => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      const message = {
        Text: messageText,
        CreatedAt: new Date().toISOString()
      };
      socket.send(JSON.stringify(message));
    }
  };

  return { messages, sendMessage, connectionStatus, error, connectWebSocket };
};

const Chat = () => {
  const [messageInput, setMessageInput] = useState('');
  const [user, setUser] = useState(null);
  const { messages, sendMessage, connectionStatus, error, connectWebSocket } = useChat();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
    const username = localStorage.getItem('username');

    if (token && userId && username) {
      setUser({ id: userId, username });
      connectWebSocket(token);
    }
  }, [connectWebSocket]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (messageInput.trim() && user) {
      sendMessage(messageInput);
      setMessageInput('');
    }
  };

  if (!user) {
    return (
      <div className="chat-container">
        <div className="auth-warning">
          Please login to access the chat
        </div>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <div className="header">
        <h2>Forum Chat</h2>
        <div className={`status ${connectionStatus}`}>
          {connectionStatus.toUpperCase()}
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="messages">
        {messages.map((msg, index) => (
          <div 
            key={index}
            className={`message ${msg.UserID === user.id ? 'own' : 'other'}`}
          >
            <div className="message-header">
              <span className="username">{msg.Username}</span>
              <span className="time">
                {new Date(msg.CreatedAt).toLocaleTimeString()}
              </span>
            </div>
            <div className="content">{msg.Text}</div>
          </div>
        ))}
      </div>

      <form className="message-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={messageInput}
          onChange={(e) => setMessageInput(e.target.value)}
          placeholder="Type your message..."
          disabled={connectionStatus !== 'connected'}
        />
        <button 
          type="submit"
          disabled={connectionStatus !== 'connected' || !messageInput.trim()}
        >
          Send
        </button>
      </form>
    </div>
  );
};

