import React, { useState } from 'react';
import useWebSocket from '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/hooks/useWebSocket.js';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Chat = () => {
    const [message, setMessage] = useState('');
    const userId = localStorage.getItem('userId');
    const token = localStorage.getItem('token'); // Ensure this is the JWT token
    const wsUrl = `ws://localhost:8082/websocket?token=${encodeURIComponent(token)}`;

    const { 
        messages, 
        sendMessage, 
        connectionStatus 
    } = useWebSocket(wsUrl);

    if (!userId) {
        return <div>Please login to use chat</div>;
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim()) {
            sendMessage({
                content: message,
                userId: userId,
                timestamp: new Date().toISOString()
            });
            setMessage('');
        }
    };
    useEffect(() => {
      if (error) {
          alert(`Chat error: ${error}`);
      }
  }, [error]);
    return (
        <div className="chat-container">
            <div className="connection-status">
                Status: {connectionStatus}
            </div>
            <div className="messages">
                {messages.map((msg, index) => (
                    <div key={index} className="message">
                        <span className="user">{msg.userId}</span>
                        <span className="content">{msg.content}</span>
                    </div>
                ))}
            </div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    disabled={connectionStatus !== 'connected'}
                />
                <button 
                    type="submit"
                    disabled={connectionStatus !== 'connected'}
                >
                    Send
                </button>
            </form>
        </div>
    );
};
export default Chat;
