import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Comments = ({ postId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [userCache, setUserCache] = useState({});
    const token = localStorage.getItem('token');

    // Получение информации о пользователе
    const fetchUser = useCallback(async (userId) => {
        try {
            if (userCache[userId]) return;

            const response = await axios.get(`http://localhost:8081/api/v1/users/${userId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setUserCache(prev => ({
                ...prev,
                [userId]: response.data.username
            }));
        } catch (error) {
            setUserCache(prev => ({
                ...prev,
                [userId]: `User #${userId}`
            }));
        }
    }, [token, userCache]);

    // Получение комментариев
    const fetchComments = useCallback(async () => {
        try {
            setLoading(true);
            const response = await axios.get(
                `http://localhost:8081/api/v1/posts/${postId}/comments`,
                { headers: { Authorization: `Bearer ${token}` } }
            );

            // Получаем уникальные ID авторов
            const authorIds = [...new Set(response.data.map(c => c.author_id))];

            // Загружаем информацию о пользователях
            await Promise.all(authorIds.map(fetchUser));

            // Обновляем комментарии с именами пользователей
            const commentsWithUsernames = response.data.map(comment => ({
                ...comment,
                username: userCache[comment.author_id] || `User #${comment.author_id}`
            }));

            setComments(commentsWithUsernames);
        } catch (err) {
            setError(err.response?.data?.error || err.message);
        } finally {
            setLoading(false);
        }
    }, [postId, token, fetchUser, userCache]);

    // Создание комментария
    const handleSubmitComment = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return;

        try {
            const currentUser = JSON.parse(localStorage.getItem('user'));
            const response = await axios.post(
                `http://localhost:8081/api/v1/posts/${postId}/comments`,
                { content: newComment },
                { headers: { 
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json' 
                }}
            );

            // Добавляем комментарий с именем пользователя
            const newCommentWithAuthor = {
                ...response.data,
                username: currentUser?.username || `User #${response.data.author_id}`
            };

            setComments(prev => [newCommentWithAuthor, ...prev]);
            setNewComment('');
        } catch (err) {
            setError(err.response?.data?.error || err.message);
        }
    };

    useEffect(() => {
        fetchComments();
    }, [fetchComments]);

    return (
        <div className="comments-section">
            <h4>Comments ({comments.length})</h4>

            <form onSubmit={handleSubmitComment} className="comment-form">
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Write a comment..."
                    rows="3"
                />
                <button type="submit" className="submit-comment-btn">
                    Post Comment
                </button>
            </form>

            {loading && <div>Loading comments...</div>}
            {error && <div className="error">{error}</div>}

            <div className="comments-list">
                {comments.map(comment => (
                    <div key={comment.id} className="comment-item">
                        <div className="comment-header">
                            <span className="comment-author">
                                {comment.username}
                            </span>
                        </div>
                        <p className="comment-content">{comment.content}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Comments;
