import React, { useState, useEffect, useRef } from 'react';
import useWebSocket from 'react-use-websocket';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Chat = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [connectionStatus, setConnectionStatus] = useState('connecting');
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const navigate = useNavigate();
    const messagesEndRef = useRef(null);

    const token = localStorage.getItem('token');
    const username = localStorage.getItem('username');
    const isAuthenticated = !!token;

    const { sendMessage, lastMessage, readyState } = useWebSocket(
        'ws://localhost:8082/ws',
        {
            onOpen: () => {
                console.log('WebSocket connection established');
                setConnectionStatus('connected');
                setError(null);
            },
            onClose: () => {
                console.log('WebSocket connection closed');
                setConnectionStatus('disconnected');
                setError('Connection lost. Reconnecting...');
            },
            onError: (event) => {
                console.error('WebSocket error:', event);
                setConnectionStatus('error');
                setError('Failed to connect to chat server');
            },
            shouldReconnect: (closeEvent) => true,
            reconnectAttempts: 10,
            reconnectInterval: 3000,
        }
    );

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get('http://localhost:8082/messages');
                setMessages(response.data || []);
                setIsLoading(false);
            } catch (error) {
                console.error('Error fetching messages:', error);
                setError('Failed to load chat history');
                setIsLoading(false);
            }
        };

        if (isAuthenticated) {
            fetchMessages();
        } else {
            setIsLoading(false);
        }
    }, [isAuthenticated]);

    useEffect(() => {
        if (lastMessage !== null) {
            try {
                const newMessage = JSON.parse(lastMessage.data);
                setMessages(prev => [...prev, newMessage]);
            } catch (err) {
                console.error('Error parsing message:', err);
            }
        }
    }, [lastMessage]);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    const handleSendMessage = () => {
        if (!isAuthenticated) {
            navigate('/login');
            return;
        }

        if (!message.trim()) return;

        const msg = {
            username: username || 'Anonymous',
            message: message.trim(),
            timestamp: new Date().toISOString()
        };

        sendMessage(JSON.stringify(msg));
        setMessage('');
    };

    const connectionStatusColors = {
        connecting: 'orange',
        connected: 'green',
        disconnected: 'red',
        error: 'red'
    };

    if (!isAuthenticated) {
        return (
            <div className="chat-container">
                <div className="auth-required-message">
                    <p>Please <a href="/login">login</a> to access the chat</p>
                </div>
            </div>
        );
    }

    if (isLoading) {
        return (
            <div className="chat-container">
                <div className="loading-message">Loading chat...</div>
            </div>
        );
    }

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>Community Chat</h2>
                <div 
                    className="connection-status" 
                    style={{ backgroundColor: connectionStatusColors[connectionStatus] }}
                >
                    {connectionStatus.toUpperCase()}
                </div>
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="messages-window">
                {messages.length > 0 ? (
                    messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.username === username ? 'own-message' : ''}`}>
                            <div className="message-header">
                                <span className="message-username">{msg.username}</span>
                                <span className="message-time">
                                    {new Date(msg.timestamp).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                                </span>
                            </div>
                            <div className="message-content">{msg.message}</div>
                        </div>
                    ))
                ) : (
                    <div className="no-messages">No messages yet. Be the first to say hello!</div>
                )}
                <div ref={messagesEndRef} />
            </div>

            <div className="message-input-area">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Type your message..."
                    disabled={connectionStatus !== 'connected'}
                />
                <button
                    onClick={handleSendMessage}
                    disabled={!message.trim() || connectionStatus !== 'connected'}
                >
                    Send
                </button>
            </div>
        </div>
    );
};

export default Chat;
