import React, { useState, useEffect } from 'react';
import useWebSocket from '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/hooks/useWebSocket.js';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Chat = () => {
    const [messageInput, setMessageInput] = useState('');
    const [userData, setUserData] = useState(null);
    const token = localStorage.getItem('token');
    const wsUrl = `ws://localhost:8082/ws`;

    const {
        messages,
        sendMessage,
        connectionStatus,
        error
    } = useWebSocket(wsUrl, token);

    // Загрузка данных пользователя при монтировании
    useEffect(() => {
        const userId = localStorage.getItem('userId');
        const username = localStorage.getItem('username');
        if (userId && username) {
            setUserData({ id: userId, username });
        }
    }, []);

    // Обработка ошибок
    useEffect(() => {
        if (error) {
            console.error('Chat error:', error);
        }
    }, [error]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (messageInput.trim() && userData) {
            sendMessage({
                Text: messageInput,
                UserID: userData.id,
                Username: userData.username,
                CreatedAt: new Date().toISOString()
            });
            setMessageInput('');
        }
    };

    if (!userData) {
        return (
            <div className="chat-container">
                <div className="auth-warning">
                    Please login to access the chat
                </div>
            </div>
        );
    }

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>Global Chat</h2>
                <div className={`connection-status ${connectionStatus}`}>
                    Status: {connectionStatus.toUpperCase()}
                </div>
            </div>

            <div className="messages-list">
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.UserID === userData.id ? 'own-message' : ''}`}>
                        <div className="message-header">
                            <span className="username">{message.Username}</span>
                            <span className="timestamp">
                                {new Date(message.CreatedAt).toLocaleTimeString()}
                            </span>
                        </div>
                        <div className="message-content">
                            {message.Text}
                        </div>
                    </div>
                ))}
            </div>

            <form className="message-form" onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={messageInput}
                    onChange={(e) => setMessageInput(e.target.value)}
                    placeholder="Type your message..."
                    disabled={connectionStatus !== 'connected'}
                    aria-label="Type your message"
                />
                <button
                    type="submit"
                    disabled={!messageInput.trim() || connectionStatus !== 'connected'}
                    className="send-button"
                >
                    {connectionStatus === 'connected' ? 'Send' : 'Connecting...'}
                </button>
            </form>

            {error && (
                <div className="error-message">
                    Error: {error}
                </div>
            )}
        </div>
    );
};

export default Chat;
