import React, { useState } from 'react';
import axios from 'axios'; // Для отправки запросов на сервер
import useWebSocket from '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/hooks/useWebSocket.js';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [email, setEmail] = useState(''); // Новое состояние для email
  const [password, setPassword] = useState(''); // Новое состояние для password
  const [errorMessage, setErrorMessage] = useState('');

  const userId = localStorage.getItem('userId');
  const username = localStorage.getItem('username');

  const { 
    messages, 
    sendMessage, 
    connectionStatus 
  } = useWebSocket('ws://localhost:8082/ws');

  if (response.status === 200) {
    const { userId, username, token } = response.data;

    // Добавляем проверку на наличие username
    if (!username) {
      setErrorMessage('Server did not provide username');
      return;
    }

    localStorage.setItem('userId', userId);
    localStorage.setItem('username', username); // Убедитесь что это поле приходит
    localStorage.setItem('token', token);

    window.location.href = '/chat';
  }
  if (!userId || !username) {
    return (
      <div>
        <h2>Login to use Chat</h2>
        <form onSubmit={handleLogin}>
          <div>
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>

        {errorMessage && <p>{errorMessage}</p>}
      </div>
    );
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!message.trim()) return;

    sendMessage({
      user_id: Number(userId),
      username: username,
      content: message.trim(),
    });

    setMessage('');
  };

  return (
    <div className="chat-container">
      <div className="connection-status">
        Status: {connectionStatus}
      </div>

      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index} className="message">
            <span className="user">{msg.username}:</span>  
            <span className="content">{msg.content}</span>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} className="chat-form">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          disabled={connectionStatus !== 'connected'}
          className="chat-input"
        />
        <button 
          type="submit"
          disabled={connectionStatus !== 'connected' || !message.trim()}
          className="chat-send-button"
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default Chat;
