import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import '/Users/darinautalieva/Desktop/GOProject/forum-frontend/src/components/MainLayout.css';

const Comments = ({ postId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const token = localStorage.getItem('token');
    const currentUser = JSON.parse(localStorage.getItem('user')) || {};

    const fetchComments = useCallback(async () => {
        try {
            setLoading(true);
            setError(null);

            const response = await axios.get(
                `http://localhost:8081/api/v1/posts/${postId}/comments`,
                {
                    headers: { 
                        'Accept': 'application/json',
                        'Authorization': `Bearer ${token}`
                    }
                }
            );

            const processedComments = (response.data.data || response.data).map(comment => ({
                id: parseInt(comment.id, 10),
                author_id: parseInt(comment.author_id, 10),
                post_id: parseInt(comment.post_id, 10),
                content: comment.content || '', // Добавляем значение по умолчанию
                author_name: comment.author_name || `User #${comment.author_id}`,
                created_at: comment.created_at || new Date().toLocaleDateString('en-US')
            }));

            setComments(processedComments);
        } catch (err) {
            setError(err.response?.data?.error || err.message);
        } finally {
            setLoading(false);
        }
    }, [postId, token]);

   // В компоненте Comments
const handleSubmitComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    try {
        const response = await axios.post(
            `http://localhost:8081/api/v1/posts/${postId}/comments`,
            { 
                content: newComment,
                author_id: currentUser.id // Добавляем author_id в запрос
            },
            { 
                headers: { 
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json' 
                }
            }
        );

        // Добавляем полные данные комментария с именем пользователя
        const newCommentWithAuthor = {
            ...response.data,
            author_name: currentUser.username || `User #${currentUser.id}`,
            created_at: new Date().toISOString()
        };

        setComments(prev => [newCommentWithAuthor, ...prev]);
        setNewComment('');
    } catch (err) {
        setError(err.response?.data?.error || err.message);
    }
};

    useEffect(() => {
        fetchComments();
    }, [fetchComments]);

    return (
        <div className="comments-section">
            <h4>Comments ({comments.length})</h4>

            <form onSubmit={handleSubmitComment} className="comment-form">
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Write a comment..."
                    rows="3"
                />
                <button 
                    type="submit" 
                    className="submit-comment-btn"
                    disabled={loading}
                >
                    {loading ? 'Posting...' : 'Post Comment'}
                </button>
            </form>

            {loading && <div className="loading-indicator">Loading comments...</div>}
            {error && <div className="error-message">Error: {error}</div>}

            <div className="comments-list">
                {comments.map(comment => (
                    <div key={comment.id} className="comment-item">
                        <div className="comment-header">
                            <span className="comment-author">
                                {comment.author_name || `User #${comment.author_id}`}
                            </span>
                            <span className="comment-timestamp">
                                {new Date(comment.created_at).toLocaleDateString('en-US', {
                                    hour: '2-digit',
                                    minute: '2-digit'
                                })}
                            </span>
                        </div>
                        <p className="comment-content">
                        {(comment.content || '').split('\n').map((p, i) => (
                            <span key={i}>{p}<br/></span>
                        ))}
                    </p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Comments;
